mapper.select:
SELECT r.id, r.user_id AS userId, u.username, u.name, u.phone, u.email, r.status, r.created_at AS createdAt, r.checkin_at AS checkinAt FROM registrations r LEFT JOIN users u ON r.user_id = u.id WHERE r.event_id = #{eventId}
AND r.status = #{status}
AND ( u.username LIKE CONCAT('%', #{keyword}, '%') OR u.name LIKE CONCAT('%', #{keyword}, '%') OR u.phone LIKE CONCAT('%', #{keyword}, '%') )
ORDER BY r.created_at DESC
